import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/header';
import SearchBar from './components/searchbar';
import Hero from './components/hero';
function App() {
  const [courses, setCourses] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');


  useEffect(() => {
    fetchCourses();
  }, []);

  // Fetch courses from University Domains API and transform to course format
  const fetchCourses = async () => {
    try {
      // Using a combination of APIs to get course-like data
      // We'll use the JSONPlaceholder API for realistic data
      const response = await fetch('https://jsonplaceholder.typicode.com/posts?_limit=12');
      const posts = await response.json();

      // Transform the API data into course format
      const categories = ['Development', 'Design', 'Data Science', 'Marketing', 'Business'];
      const levels = ['Beginner', 'Intermediate', 'Advanced'];

      const transformedCourses = posts.map((post, index) => ({
        id: post.id,
        name: post.title.split(' ').slice(0, 4).join(' ').charAt(0).toUpperCase() + 
              post.title.split(' ').slice(0, 4).join(' ').slice(1),
        category: categories[index % categories.length],
        duration: `${Math.floor(Math.random() * 8) + 6} weeks`,
        level: levels[index % levels.length],
        description: post.body.substring(0, 80) + '...',
        enrolled: Math.floor(Math.random() * 3000) + 500
      }));

      setCourses(transformedCourses);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching courses:', error);
      // Fallback to local data if API fails
      const fallbackCourses = [
        {
          id: 1,
          name: "Web Development Fundamentals",
          category: "Development",
          duration: "12 weeks",
          level: "Beginner",
          description: "Master HTML, CSS, and JavaScript to build modern websites",
          enrolled: 2847
        },
        {
          id: 2,
          name: "React & Modern JavaScript",
          category: "Development",
          duration: "10 weeks",
          level: "Intermediate",
          description: "Build dynamic web applications with React and ES6+",
          enrolled: 1923
        },
        {
          id: 3,
          name: "UI/UX Design Principles",
          category: "Design",
          duration: "8 weeks",
          level: "Beginner",
          description: "Learn design thinking and create beautiful user interfaces",
          enrolled: 1456
        },
        {
          id: 4,
          name: "Data Science with Python",
          category: "Data Science",
          duration: "14 weeks",
          level: "Intermediate",
          description: "Analyze data and build machine learning models",
          enrolled: 2103
        },
        {
          id: 5,
          name: "Digital Marketing Mastery",
          category: "Marketing",
          duration: "6 weeks",
          level: "Beginner",
          description: "Master SEO, social media, and content marketing",
          enrolled: 1678
        },
        {
          id: 6,
          name: "Cloud Architecture",
          category: "Development",
          duration: "10 weeks",
          level: "Advanced",
          description: "Design scalable cloud solutions with AWS and Azure",
          enrolled: 892
        }
      ];
      setCourses(fallbackCourses);
      setIsLoading(false);
    }
  };

  // Filter courses based on search and category
  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         course.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         course.category.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || course.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = ['all', ...new Set(courses.map(c => c.category))];


  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-orange-50 to-rose-50">
      <Header />

      <SearchBar 
        searchQuery={searchQuery} 
        setSearchQuery={setSearchQuery} 
      />

      <Hero 
        courses={filteredCourses}
        categories={categories}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
      />

    </div>
  );
}

export default App;
