import React, { useState, useEffect } from 'react';
import { BookOpen } from 'lucide-react';

const Header = () => {
  const [quote, setQuote] = useState({ text: '', author: '' });

  useEffect(() => {
    fetchMotivationalQuote();
  }, []);

  const fetchMotivationalQuote = async () => {
    try {
      const response = await fetch('https://api.quotable.io/random?tags=inspirational');
      const data = await response.json();
      setQuote({ text: data.content, author: data.author });
    } catch (error) {
      setQuote({ 
        text: "Education is the most powerful weapon which you can use to change the world.", 
        author: "Nelson Mandela" 
      });
    }
  };

  return (
    <header className="grid-pattern pt-16 pb-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="hero-title text-5xl sm:text-6xl lg:text-7xl mb-4">
            Course Catalog
          </h1>
          <p className="subtitle text-xl sm:text-2xl text-orange-800 font-medium">
            Discover your path to mastery
          </p>
        </div>

        {/* Motivational Quote */}
        <div className="quote-card max-w-3xl mx-auto p-8 rounded-2xl shadow-lg">
          <div className="flex items-start gap-4">
            <div className="flex-shrink-0">
              <div className="w-12 h-12 rounded-full bg-gradient-to-br from-orange-500 to-rose-600 flex items-center justify-center">
                <BookOpen className="text-white" size={24} />
              </div>
            </div>
            <div className="flex-1">
              <blockquote className="text-lg sm:text-xl text-gray-800 font-medium italic mb-2">
                "{quote.text}"
              </blockquote>
              <cite className="text-orange-700 font-semibold not-italic">â€” {quote.author}</cite>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
