import React, { useState, useEffect } from 'react';

const Header = () => {
  const [quote, setQuote] = useState({ text: '', author: '' });

  useEffect(() => {
    fetchMotivationalQuote();
  }, []);

  const fetchMotivationalQuote = async () => {
    try {
      const response = await fetch('https://api.quotable.io/random?tags=inspirational');
      const data = await response.json();
      setQuote({ text: data.content, author: data.author });
    } catch (error) {
      setQuote({ 
        text: "Education is the most powerful weapon which you can use to change the world.", 
        author: "Nelson Mandela" 
      });
    }
  };

  return (
    <header className="grid-pattern pt-16 pb-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="hero-title text-5xl sm:text-6xl lg:text-7xl mb-4">
            Course Catalog
          </h1>
          <p className="subtitle text-xl sm:text-2xl text-orange-800 font-medium">
            Discover your path to mastery
          </p>
        </div>


      </div>
    </header>
  );
};

export default Header;
